// client/apiCalls/index.js
import axios from "axios";

export const url = "http://localhost:5000";

export const axiosInstance = axios.create({
  baseURL: url,
  // no static headers here
  withCredentials: true, // only if you rely on cookies; safe to include
});

// ensure latest token is attached to each request
axiosInstance.interceptors.request.use(
  (config) => {
    try {
      const token = localStorage.getItem("token");
      if (token) {
        config.headers = config.headers || {};
        config.headers.Authorization = `Bearer ${token}`;
      } else {
        // ensure header is removed if no token
        if (config.headers) delete config.headers.Authorization;
      }
    } catch (err) {
      // ignore; proceed without token
    }
    return config;
  },
  (error) => Promise.reject(error)
);
